Commands.Handler:
function (args) {
  var count = state.get(StateKeys.counter) || 0;
  count++;
  state.set(StateKeys.counter, count);
  output.print('Counter incremented to: ' + count);
}

function (args) {
  state.set(StateKeys.customOption, 'default-value');
  state.set(StateKeys.counter, 0);
  output.print('State reset to defaults');
}
